if (msg['PID']['PID.19']['PID.19.1']) {
	tmp['patient']['identifiers'].push({
		"type": "ssn",
		"value": validate(msg['PID']['PID.19']['PID.19.1'], '', new Array())
	});
	tmp['patient']['identifiers'].push({
		"type": "ssn4",
		"value": validate(msg['PID']['PID.19']['PID.19.1'], '', new Array()).slice(-4)
	});
}

switch (validate(msg['IN1'][i]['IN1.15']['IN1.15.1'], '', new Array())) {
	case "mbi":
	case "has-private-insurance":
	case "has-self-pay":
	case "has-medicare":
	case "has-medicaid":
	case "has-medicare-advantage":		tmp['patient']['identifiers'].push({
			"type": validate(msg['IN1'][i]['IN1.15']['IN1.15.1'], '', new Array()),
			"value": validate(msg['IN1'][i]['IN1.2']['IN1.2.1'], '', new Array())
		});
		break;
	case "medicaid-number":
		tmp['patient']['identifiers'].push({
			"type": "medicaid-number",
			"value": validate(msg['IN1'][i]['IN1.2']['IN1.2.1'], '', new Array())
		});
		tmp['patient']['identifiers'].push({
			"type": "medicaid-state",
			"value": validate(msg['IN1'][i]['IN1.5']['IN1.5.4'], '', new Array())
		});
		break;}

switch (msg['PID']['PID.3'][i]['PID.3.5'].toString()) {
	case 'PI':		tmp['patient_hchb']['patientId'] = validate(msg['PID']['PID.3'][i]['PID.3.1'], '', new Array());
		break;
	case 'PN':
		tmp['patient_hchb']['admissionId'] = validate(msg['PID']['PID.3'][i]['PID.3.1'], '', new Array());
		break;}

