properties.code:
/**
	Get current date and time

	@return {String} return description
*/
function GetCurrentHL7DateTime() {

	var dateFormat = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	var dateNow = new Packages.java.util.Date();
	return dateFormat.format(dateNow).toString() + "";
}

/**
Modify the description here. Modify the function name and parameters as needed. One function per
template is recommended; create a new code template for each new function.

@padchars Example 0000000000  pad with 0s to 10 digits total
@str your input string
@padLeft 1 for left or 0 for right

@return {String} return description
*/
function PadString(padchars, str, padLeft)
{
	if (typeof str === 'undefined')
		return padchars;
	if (padLeft) {
		return (padchars + str).slice(-padchars.length);
	} else {
		return (str + padchars).substring(0, padchars.length);
	}
}
